mapper.select:
SELECT COUNT(COMMENT_ID)
FROM TBL_COMMENT_LIKE
WHERE COMMENT_ID= #{commentId}

SELECT COUNT(*)
FROM TBL_COMMENT_LIKE
WHERE COMMENT_ID = #{commentId}
AND USER_ID = #{userId}

mapper.delete:
DELETE FROM TBL_COMMENT_LIKE
WHERE COMMENT_ID = #{commentId}
AND USER_ID = #{userId}

mapper.select:
SELECT COUNT(*)
FROM TBL_COMMENT_LIKE
WHERE COMMENT_ID = #{commentId}
