mapper.select:
SELECT ID, QUIZ_DIFFICULT as quizDifficult, QUIZ_LANGUAGE as quizLanguage, QUIZ_TITLE as quizTitle, QUIZ_CATEGORY as quizCategory, QUIZ_EXP as quizExp, QUIZ_DESCRIPTION as quizDescription FROM TBL_QUIZ WHERE 1 = 1
AND QUIZ_LANGUAGE = #{quizLanguage}
AND QUIZ_DIFFICULT = #{quizDifficult}
AND (QUIZ_TITLE LIKE CONCAT('%', #{keyword}, '%') OR QUIZ_DESCRIPTION LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY ID ASC
OFFSET (#{page} - 1) * 10 ROWS FETCH NEXT 10 ROWS ONLY

SELECT ID, QUIZ_TITLE, QUIZ_DESCRIPTION, QUIZ_CATEGORY, QUIZ_DIFFICULT, QUIZ_LANGUAGE, QUIZ_EXPECTATION, QUIZ_EXP
FROM TBL_QUIZ
WHERE ID = #{id}

SELECT QUIZ_EXPECTATION
FROM TBL_QUIZ
WHERE ID = #{id}

mapper.insert:
UPDATE TBL_QUIZ_PERSONAL
SET QUIZ_PERSONAL_IS_BOOKMARK = #{isSolve}, QUIZ_PERSONAL_IS_SOLVE = #{isBookmark}
WHERE ID = #{id}
